    private void BotonGuardarProfActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonGuardarProfActionPerformed
        String codigo = TextFieldCedulaProf.getText().trim();
        String nombres = TextFieldNombreProf.getText().trim();
        String apellidos = TextFieldApellidoProf.getText().trim();
        String carrera = TextFieldCarreraProf.getText().trim();
        String direccion = TextFieldDireccionProf.getText().trim();
        String telefono = TextFieldTelefonoProf.getText().trim();

        // Validar que todos los campos estén llenos
        if (codigo.isEmpty() || nombres.isEmpty() || apellidos.isEmpty() || carrera.isEmpty()
                || direccion.isEmpty() || telefono.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Por favor, complete todos los campos.", "Error", JOptionPane.ERROR_MESSAGE);
            return;
        }

        // Crear el estudiante
        Profesor profesor = new Profesor();
        profesor.setNombres(nombres);
        profesor.setApellidos(apellidos);
        profesor.setCarrera(carrera);
        profesor.setDireccion(direccion);
        profesor.setTelefono(telefono);

        // Insertar el estudiante usando la lógica de gestionEstudiantes
        if (gestionProfesores.insertarEstudiante(codigo, profesor)) {
            limpiarCampos();
            JOptionPane.showMessageDialog(this, "Estudiante guardado correctamente.", "Éxito", JOptionPane.INFORMATION_MESSAGE);
        } else {
            JOptionPane.showMessageDialog(this, "El estudiante con el código " + codigo + " ya existe.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_BotonGuardarProfActionPerformed

