    private void limpiarCampos() {
        TextFieldCedulaProf.setText("");
        TextFieldNombreProf.setText("");
        TextFieldApellidoProf.setText("");
        TextFieldCarreraProf.setText("");
        TextFieldDireccionProf.setText("");
        TextFieldTelefonoProf.setText("");
    }
    
